package main

import "fmt"

func countXMAS(grid [][]rune) int {
	masPatterns := []string{"MAS", "SAM"}
	count := 0
	rows := len(grid)
	cols := len(grid[0])

	fmt.Println("Grid dimensions:", rows, "x", cols)

	isMAS := func(s string) bool {
		for _, pattern := range masPatterns {
			if s == pattern {
				return true
			}
		}
		return false
	}

	for i := 1; i < rows-1; i++ {
		for j := 1; j < cols-1; j++ {
			topLeft := string([]rune{grid[i-1][j-1], grid[i][j], grid[i+1][j+1]})
			topRight := string([]rune{grid[i-1][j+1], grid[i][j], grid[i+1][j-1]})

			if isMAS(topLeft) && isMAS(topRight) {
				count++
			}
		}
	}

	return count
}

func buildGrid(input string) [][]rune {
	var grid [][]rune
	rows := []rune(input)

	row := []rune{}
	for _, char := range rows {
		if char == '\n' {
			grid = append(grid, row)
			row = []rune{}
		} else {
			row = append(row, char)
		}
	}

	if len(row) > 0 {
		grid = append(grid, row)
	}

	fmt.Println("Grid built with", len(grid), "rows")
	return grid
}

